int brute(int N,int A,int B){
    int res=0;
    for (int mask=0;mask<(1<<N);++mask){
        auto d=[&](int i){return (mask&1<<i)>>i;};
        vector<pair<int,int>> v;
        int cnt=1;
        for (int i=0;i+1<N;++i){
            if (d(i)!=d(i+1)){
                v.emplace_back(d(i),cnt);
                cnt=1;
            } else ++cnt;
        }
        v.emplace_back(d(N-1),cnt);
        for (auto &p:v){
            if (p.first==1&&p.second>=B){
                p.first=0;
            }
        }
        int now=0,ok=0;
        for (auto p:v){
            if (p.first==1) now=0;
            else {
                now+=p.second;
                if (now>=A) ok=1;
            }
        }
        // debug(mask,ok,v);
        res+=ok;
    }
    return res;
}

