const int mod = 1e9 + 7;

inline int Mod(int x) { return x >= mod ? x - mod : x; }
inline void Add(int &x, int y) { x += y, x -= x >= mod ? mod : 0; }

int f[5010];
int g[5010];
int P[5010];
int M[5010];
int F[2][5010];

int main() {
#ifdef LOCAL
	FILE("");
#endif

	int n = ri, A = ri, B = ri, res = 1;
	for(int i = 1; i <= n; i++) res = Mod(res << 1);
	if(A == 1 || B == 1) return printf("%d\n", res), 0;
	if(A > B) swap(A, B);
	f[0] = 1, g[0] = 1;
	for(int i = A; i < B; i++) {
		f[i] = Mod(f[i - 1] + g[i - 1]);
		for(int j = A; j <= i; j++)
			Add(g[i], f[i - j]);
	}

	for(int i = 1; i < B; i++)
		for(int j = 1; j <= i; j++)
			P[i] = (P[i] + 1LL * g[i - j] * (i == j ? 1 : j)) % mod;
	for(int i = 1; i < B; i++)
		for(int j = 1; j <= i; j++)
			M[i] = (M[i] + 1LL * g[i - j] * (i == j ? 1 : j - 1)) % mod;

	F[0][0] = F[1][0] = 1;
	for(int i = 1; i <= n; i++) {
		for(int j = 1; j < B; j++) {
			if(i == j || i == n) {
				F[1][i] = (F[1][i] + 1LL * F[0][i - j] * P[j]) % mod;
				if(i == j) break;
			} else F[1][i] = (F[1][i] + 1LL * F[0][i - j] * M[j]) % mod;
		}
		for(int j = 1; j < A; j++) {
			Add(F[0][i], F[1][i - j]);
			if(i == j) break;
		}
	}

	cout << Mod(res + mod - Mod(F[0][n] + F[1][n])) << endl;
	return 0;
}

