int fact[10005],ifact[10005];
inline int ncr(int n, int r) {
	if(n<r||r<0||n<0)
		return 0;
	return (((fact[n]*ifact[r])%mod)*ifact[n-r])%mod;
}
int dp[5005];
void solve() {
	fact[0]=1;
	fr(i,1,10000)
		fact[i]=(fact[i-1]*i)%mod;
	ifact[10000]=powm(fact[10000],mod-2);
	for(int i=9999; i>=0; i--)
		ifact[i]=(ifact[i+1]*(i+1))%mod;
	int n,a,b;
	cin>>n>>a>>b;
	if(a!=b)
		exit(1);
	if(a<b)
		swap(a,b);
	int lol=1;
	fr(i,1,a) {
		int places=a-b*i-i+1;
		int r=2*i+1;
		lol=(lol+ncr(places+r-1,r-1))%mod;
	}
	int ans=0;
	fr(i,a,n) {
		int times=powm(2,i-a);
		fr(j,a,i-a) {
			int minus=(dp[j]*powm(2,i-a-j))%mod;
			times=(times-minus)%mod;
		}
		rep(j,i-a+1,i)
			times=(times-dp[j])%mod;
		dp[i]=times;
		trace(times);
		ans=(ans+times*powm(2,n-i))%mod;
	}
	trace(lol,ans);
	if(ans<0)
		ans+=mod;
	cout<<(ans*lol)%mod<<endl;
}

