#define ck(x) (x>=mod?x-mod:x) 
int qpow(int a,int b){
	int ans=1;
	while(b){
		if(b&1)ans=1LL*ans*a%mod;
		a=1LL*a*a%mod;
		b>>=1;
	}
	return ans;
}
int main(){
	n=read(),A=read(),B=read();
	if(A>B)swap(A,B);
	f[0][0]=f[0][1]=1;
	for(int i=1;i<B;++i){
		for(int j=A;j<=i;++j){
			f[i][0]=ck(f[i][0]+f[i-j][1]);
		}
		for(int j=1;j<=i;++j){
			f[i][1]=ck(f[i][1]+f[i-j][0]);
		}
	}
	g[0]=1;
	for(int i=1;i<B;++i){
		g[i]=ck(f[i][0]+f[i][1]);
	}
	for(int i=1;i<n;++i){
		dp[i][0]+=(i<A);
		dp[i][1]+=(i<B?f[i][1]:0);
		for(int j=1;j<min(i,A);++j){
			dp[i][0]=ck(dp[i][0]+dp[i-j][1]);
		}
		for(int j=1;j<min(i,B);++j){
			dp[i][1]=(dp[i][1]+1LL*dp[i-j][0]*(j==1?1:g[j-2]))%mod;
		}
		if(n-i<A)ans=ck(ans+dp[i][1]);
		if(n-i<B)ans=(ans+1LL*dp[i][0]*g[n-i-1])%mod;
	}
	printf("%d\n",(qpow(2,n)-ans+mod)%mod);	
	return 0;
}

#include <bits/stdc++.h>

using namespace std;

const int maxn = 5010;
const int mod = 1e9+7;

int n, A, B;
int f[maxn][2], g[maxn][2], a[maxn][maxn], h[maxn];

int calh(int l, int a, int b) {
	if (!a && !b) return h[l];
	if (l == 1) return 1;
	return h[l - a - b];
}

int main() {
	scanf("%d%d%d", &n, &A, &B);
	if (A > B) swap(A, B);
	f[0][0] = g[0][0] = 1;
	a[0][0] = 1;
	for (int i = 1; i <= n; i++) {
		for (int j = 0; j <= i; j++) {
			if (!j || j >= A) a[i][0] = (a[i][0] + a[i-1][j]) % mod;
			a[i][j+1] = (a[i][j+1] + a[i-1][j]) % mod;
		}
	}
	for (int i = 0; i <= n; i++) {
		h[i] = (h[i] + a[i][0]) % mod;
		for (int j = A; j <= i; j++) {
			h[i] = (h[i] + a[i][j]) % mod;
		}
	}
	for (int i = 1; i <= n; i++) {
		for (int k = 1; k < A; k++) {
			f[i][0] = (f[i][0] + g[i-k][0]) % mod;
			f[i][1] = (f[i][1] + g[i-k][1]) % mod;
		}
		for (int k = 1; k < B && k <= i; k++) {
			g[i][0] = (g[i][0] + 1LL * f[i-k][0] * calh(k, k < i, i != n) % mod) % mod;
		}
		for (int k = 1; k <= i; k++) {
			g[i][1] = (g[i][1] + 1LL * f[i-k][1] * calh(k, k < i, i != n) % mod) % mod;
		}
		for (int k = B; k <= i; k++) {
			g[i][1] = (g[i][1] + 1LL * f[i-k][0] * calh(k, k < i, i != n) % mod) % mod;
		}
	}
	int ans = (f[n][1] + g[n][1]) % mod;
	printf("%d\n", ans);
	return 0;
}

