    for (int i = 0; i < n; i++)
    {
        if (s[i] - '0' == cur)
        {
            cnt++;
            continue;
        }
        if (cur == 0)
        {
            if (cnt < a)
                return false;
        }
        cur = s[i] - '0';
        cnt = 1;
    }
    if (cur == 0)
    {
        if (cnt < a)
            return false;
    }
    return true;
}

bool ok_b(string s)
{
    bool has = false;
    for (int i = 0; i < s.size(); i++)
        has |= (s[i] == '1');
    if (!has)
        return false;
    int cur = -1;
    int cnt = 0;
    for (int i = 0; i < n; i++)
    {
        if (s[i] - '0' == cur)
        {
            cnt++;
            continue;
        }
        if (cur == 1)
        {
            if (cnt < b)
                return false;
        }
        cur = s[i] - '0';
        cnt = 1;
    }
    if (cur == 1)
    {
        if (cnt < b)
            return false;
    }
    return true;
}

bool check(string s)
{
    for (int i = 0; i < n; i++)
        for (int j = i; j < n; j++)
            if (j - i + 1 >= a)
                if (ok_b(s.substr(i, j - i + 1)))
                    return true;
    for (int i = 0; i < n; i++)
        for (int j = i; j < n; j++)
            if (j - i + 1 >= b)
                if (ok_a(s.substr(i, j - i + 1)))
                    return true;
    return false;
}

void slow()
{
    int ans = 0;
    for (int mask = 0; mask < (1 << n); mask++)
    {
        string s = "";
        for (int i = 0; i < n; i++)
            s += (char)('0' + ((mask >> i) & 1));
        if (check(s))
        {
            ans++;
            //cerr << s << "\n";
        }
    }
    cout << "correct: " << ans << "\n";
}

