    for (int i = 1; i < a; i++)
    {
        help[i] = help[i - 1];
        if (i >= b)
        {
            help[i]++;
            if (help[i] >= MOD)
                help[i] -= MOD;
        }
        for (int j = b; j + 1 <= i; j++)
        {
            help[i] += help[i - 1 - j];
            if (help[i] >= MOD)
                help[i] -= MOD;
        }
    }
    dp[0][0] = 1;
    dp[1][0] = 1;
    for (int i = 1; i <= n; i++)
    {
        dp[0][i] = 0;
        for (int j = 1; j <= min(i, a - 1); j++)
        {
            int now = 0;
            if (i == j)
            {
                if (i == n)
                    now = help[j];
                else
                    now = help[j - 1];
            }
            else
            {
                if (i == n)
                    now = help[j - 1];
                else
                {
                    if (j == 1)
                        now = 1;
                    else
                        now = help[j - 2];
                }
            }
            dp[0][i] += 1LL * dp[1][i - j] * now % MOD;
            if (dp[0][i] >= MOD)
                dp[0][i] -= MOD;
        }
        dp[1][i] = 0;
        for (int j = 1; j <= min(i, b - 1); j++)
        {
            dp[1][i] += dp[0][i - j];
            if (dp[1][i] >= MOD)
                dp[1][i] -= MOD;
        }
    }
    int bad = dp[0][n] + dp[1][n];
    if (bad >= MOD)
        bad -= MOD;
    int all = 1;
    for (int i = 0; i < n; i++)
    {
        all += all;
        if (all >= MOD)
            all -= MOD;
    }
    all -= bad;
    if (all < 0)
        all += MOD;
    cout << all << "\n";
#ifdef ONPC
    if (n <= 10)
        slow();
#endif
    return 0;
}

