using namespace std;

long long mod = 1000000007;
long long N, A, B;
long long dp[6009][6009][2];
long long ru[6009][6009];
long long power[6009];
long long Answer;

void init() {
	power[0] = 1;
	for (int i = 1; i <= 5000; i++) power[i] = (2LL * power[i - 1]) % mod;
}

void solve(int pos) {
	// 
	for (int i = 0; i <= N; i++) {
		ru[pos][i] = ru[pos - 1][i];
		ru[pos][i] += dp[pos - 1][i][0];
		ru[pos][i] %= mod;
	}

	// 
	for (int i = 0; i <= N; i++) {
		dp[pos][i][0] += dp[pos - 1][i][1];
		dp[pos][i][0] += dp[pos - 1][i][0];
		dp[pos][i][0] %= mod;
		if ((pos - i) >= A) {
			Answer += dp[pos][i][0] * power[N - pos];
			Answer %= mod;
			dp[pos][i][0] = 0;
		}
	}

	// 
	long long rem = 0;
	for (int i = 0; i <= N; i++) {
		int cl = pos - B + 1, cr = pos - 1;
		rem += (ru[cr + 1][i] - ru[cl][i] + mod) % mod;
		rem %= mod;
	}
	dp[pos][pos][1] = rem;

	// 
	for (int i = 0; i <= N; i++) {
		int cr = pos - B;
		long long val = ru[cr + 1][i];
		dp[pos][i][1] += val;
		if ((pos - i) >= A) {
			Answer += dp[pos][i][1] * power[max(0LL, N - pos - 1)];
			Answer %= mod;
			dp[pos][i][1] = 0;
		}
	}
}

int main() {
	cin >> N >> A >> B;
	if (A < B) swap(A, B);
	init();

	dp[0][0][0] = 1;
	for (int i = 1; i <= N; i++) solve(i);
	cout << Answer << endl;
	return 0;
}

