		f[i] = f[i-1];
		REP(j,1,i-a-1) add(f[i],f[j]);
	}
	REP(i,1,b-1) g[i][1] = f[i];
	REP(i,1,a-1) g[i][0] = 1;
	REP(i,a,b-1) REP(j,1,b-1-i) add(g[i+j][1],f[j]);
	REP(i,1,n) {
		REP(j,1,min(n-i,a-1)) add(g[i+j][0],g[i][1]);
		REP(j,1,min(n-i,b-1)) add(g[i+j][1],(ll)g[i][0]*f[j]);
	}
	int ans = (g[n][0]+g[n][1])%P;
	REP(i,a,b-1) REP(j,1,b-1-i) add(ans,(ll)g[n-i-j][0]*f[j]);
	ans = (qpow(2,n)-ans)%P;
	if (ans<0) ans += P;
	printf("%d\n", ans);
}

