      else{dp[0][i+j]+=ce[pt]*dp[1][i];}
      dp[0][i+j]%=mod;
    }
    for(j=1;j<b;j++){
      if((i+j)>n){break;}
      dp[1][i+j]+=dp[0][i];
      dp[1][i+j]%=mod;
    }
  }
  r+=mod;r-=dp[0][n];
  r+=mod;r-=dp[1][n];
  printf("%lld\n",r%mod);
