/** @author Spheniscine */
fun main() { _writer.solve(); _writer.flush() }
fun PrintWriter.solve() {
    val numCases = 1//readInt()
    case@ for(case in 1..numCases) {
        //print("Case #$case: ")

        val n = readInt()
        var a = readInt()
        var b = readInt()
        if(a > b) a = b.also { b = a }

        // F[x+1] = number of ways to convert x free 1s with operation A
        val F = ModIntArray(b+1)
        F[0]++
        F[1]--
        for(i in 0 .. b) {
            if(i > 0) F[i] += F[i-1]
            if(i+1 <= b) F[i+1] += F[i]
            if(i+2 <= b) F[i+2] -= F[i]
            if(i+a+1 <= b) F[i+a+1] += F[i]
        }

        val D0 = ModIntArray(n+1)
        D0[0] = ModInt(1)
        val D1 = ModIntArray(n+1)
        D1[0]++
        D1[1]--

        for(i in 0 .. n) {
            if(i > 0) D1[i] += D1[i-1]
            if(i+1 <= n) D1[i+1] += D0[i]
            if(i+a <= n) D1[i+a] -= D0[i]

            for(j in i+1 until min(i+b, n+1)) {
                var x = j - i - 2
                if(i == 0) x++
                if(j == n) x++

                D0[j] += D1[i] * F[x+1]
            }
        }

        val ans = ModInt(2).pow(n) - D0[n] - D1[n]

        println(ans.int)
    }
}

const val BILLION7 = 1e9.toInt() + 7
const val MOD = BILLION7
const val TOTIENT = MOD - 1 // assumes MOD is prime

infix fun Int.modulo(mod: Int): Int = (this % mod).let { (it shr Int.SIZE_BITS - 1 and mod) + it }
infix fun Long.modulo(mod: Long) = (this % mod).let { (it shr Long.SIZE_BITS - 1 and mod) + it }
infix fun Long.modulo(mod: Int) = modulo(mod.toLong()).toInt()

fun Int.mulMod(other: Int, mod: Int) = toLong() * other modulo mod

fun Int.powMod(exponent: Int, mod: Int): Int {
    if(exponent < 0) error("Inverse not implemented")
    var res = 1L
    var e = exponent
    var b = modulo(mod).toLong()

    while(e > 0) {
        if(e and 1 == 1) {
            res = res * b % mod
        }
        e = e shr 1
        b = b * b % mod
    }
    return res.toInt()
}

inline fun Int.toModInt() = ModInt(this modulo MOD)
