        for (int i = 1; i < B + 1; i++)
        {
            adddp[i, 1] = sum[i - 1];
        }


        long[,] dp = new long[N + 3, 2];
        dp[0, 1] = 1;
        dp[B, 0] = sum[B - 1];

        for (int i = 0; i < N + 3; i++)
        {
            for (int j = 0; j < 2; j++)
            {
                //if (dp[i, j] == 0) continue;
                for (int k = 1; k < num[j]; k++)
                {
                    if (i + k >= dp.GetLength(0)) break;
                    dp[i + k, 1 - j] += dp[i, j] * adddp[k, j];
                    dp[i + k, 1 - j] %= mod;
                }
            }
        }

        long all = 1;
        for (int i = 0; i < N; i++)
        {
            all *= 2;
            all %= mod;
        }

        long ans = all - (dp[N + 2, 0] + dp[N + 2 - B, 1] * sum[B - 1]);
        ans %= mod;
        ans += mod;
        ans %= mod;

        Console.WriteLine(ans);

    }

    void swap<T>(ref T a, ref T b)
    {
        T c = a;
        a = b;
        b = c;
    } 
}


