struct ModInt(int M_) {
  import std.conv : to;
  alias M = M_;
  int x;
  this(ModInt a) { x = a.x; }
  this(long a) { x = cast(int)(a % M); if (x < 0) x += M; }
  ref ModInt opAssign(long a) { return (this = ModInt(a)); }
  ref ModInt opOpAssign(string op)(ModInt a) {
    static if (op == "+") { x += a.x; if (x >= M) x -= M; }
    else static if (op == "-") { x -= a.x; if (x < 0) x += M; }
    else static if (op == "*") { x = cast(int)((cast(long)(x) * a.x) % M); }
    else static if (op == "/") { this *= a.inv(); }
    else static assert(false);
    return this;
  }
  ref ModInt opOpAssign(string op)(long a) {
    static if (op == "^^") {
      if (a < 0) return (this = inv()^^(-a));
      ModInt t2 = this, te = ModInt(1);
      for (long e = a; e > 0; e >>= 1) {
        if (e & 1) te *= t2;
        t2 *= t2;
      }
      x = cast(int)(te.x);
      return this;
    } else return mixin("this " ~ op ~ "= ModInt(a)");
  }
  ModInt inv() const {
    int a = x, b = M, y = 1, z = 0, t;
    for (; ; ) {
      t = a / b; a -= t * b;
      if (a == 0) {
        assert(b == 1 || b == -1);
        return ModInt(b * z);
      }
      y -= t * z;
      t = b / a; b -= t * a;
      if (b == 0) {
        assert(a == 1 || a == -1);
        return ModInt(a * y);
      }
      z -= t * y;
    }
  }
  ModInt opUnary(string op: "-")() const { return ModInt(-x); }
  ModInt opBinary(string op, T)(T a) const {
    return mixin("ModInt(this) " ~ op ~ "= a");
  }
  ModInt opBinaryRight(string op)(long a) const {
    return mixin("ModInt(a) " ~ op ~ "= this");
  }
  bool opCast(T: bool)() const { return (x != 0); }
  string toString() const { return x.to!string; }
}

enum MO = 10^^9 + 7;
alias Mint = ModInt!MO;


void main() {
  try {
    for (; ; ) {
      const N = readInt();
      int A = readInt();
      int B = readInt();
      if (A > B) {
        swap(A, B);
      }

      // (>= A) 0's and 1's
      // len, last
      auto sub = new Mint[][](N + 1, 2);
      foreach (k; A .. N + 1) {
        sub[k][0] += 1;
      }
      foreach (k; 1 .. N + 1) {
        sub[k][1] += 1;
      }
      foreach (i; 1 .. N) {
        foreach (k; A .. N - i + 1) {
          sub[i + k][0] += sub[i][1];
        }
        foreach (k; 1 .. N - i + 1) {
          sub[i + k][1] += sub[i][0];
        }
      }
      debug {
        if (N <= 10) {
          writeln("sub = ", sub);
        }
      }

      // len, last, ok
      auto dp = new Mint[][][](N + 1, 2, 2);
      foreach (k; 1 .. A) {
        dp[k][0][0] = 1;
      }
      foreach (k; 1 .. N) {
        // *1
        dp[k][1][(k >= B) ? 1 : 0] += sub[k][1];
      }
      dp[N][1][(N >= B) ? 1 : 0] += sub[N][0] + sub[N][1];
      foreach (i; 1 .. N) {
        foreach (k; 1 .. N - i + 1) {
          if (k < A) {
            foreach (t; 0 .. 2) {
              dp[i + k][0][t] += dp[i][1][t];
            }
          }
        }
        foreach (k; 1 .. N - i) {
          // 1 or 1*1
          foreach (t; 0 .. 2) {
            dp[i + k][1][(t || k >= B) ? 1 : 0] += dp[i][0][t] * ((k == 1) ? Mint(1) : sub[k - 1][1]);
          }
        }
        // 1*
        foreach (t; 0 .. 2) {
          dp[N][1][(t || N - i >= B) ? 1 : 0] += dp[i][0][t] * sub[N - i][1];
        }
      }

      Mint ans;
      foreach (s; 0 .. 2) {
        ans += dp[N][s][1];
      }
      writeln(ans);
    }
  } catch (EOFException e) {
  }
}

