const MOD: u32 = 1_000_000_007;

#[derive(Clone, Copy)]
struct ModInt(u32);

impl std::ops::Add for ModInt {
    type Output = ModInt;
    fn add(self, rhs: ModInt) -> Self::Output {
        let mut d = self.0 + rhs.0;
        if d >= MOD {
            d -= MOD;
        }
        ModInt(d)
    }
}

impl std::ops::AddAssign for ModInt {
    fn add_assign(&mut self, rhs: ModInt) {
        *self = *self + rhs;
    }
}

impl std::ops::Sub for ModInt {
    type Output = ModInt;
    fn sub(self, rhs: ModInt) -> Self::Output {
        let mut d = self.0 + MOD - rhs.0;
        if d >= MOD {
            d -= MOD;
        }
        ModInt(d)
    }
}

impl std::ops::SubAssign for ModInt {
    fn sub_assign(&mut self, rhs: ModInt) {
        *self = *self - rhs;
    }
}

impl std::ops::Mul for ModInt {
    type Output = ModInt;
    fn mul(self, rhs: ModInt) -> Self::Output {
        ModInt((self.0 as u64 * rhs.0 as u64 % MOD as u64) as u32)
    }
}

impl std::ops::MulAssign for ModInt {
    fn mul_assign(&mut self, rhs: ModInt) {
        *self = *self * rhs;
    }
}

impl std::ops::Neg for ModInt {
    type Output = ModInt;
    fn neg(self) -> Self::Output {
        ModInt(if self.0 == 0 {0} else {MOD - self.0})
    }
}

impl std::fmt::Display for ModInt {
    fn fmt<'a>(&self, f: &mut std::fmt::Formatter<'a>) -> std::fmt::Result {
        write!(f, "{}", self.0)
    }
}

impl std::str::FromStr for ModInt {
    type Err = std::num::ParseIntError;
    fn from_str(s: &str) -> Result<Self, Self::Err> {
        let val = s.parse::<u32>()?;
        Ok(ModInt::new(val))
    }
}

impl From<usize> for ModInt {
    fn from(val: usize) -> ModInt {
        ModInt((val % MOD as usize) as u32)
    }
}

#[allow(dead_code)]
impl ModInt {
    pub fn new(n: u32) -> ModInt {
        ModInt(n % MOD)
    }
    pub fn zero() -> ModInt {
        ModInt(0)
    }
    pub fn one() -> ModInt {
        ModInt(1)
    }
    pub fn pow(self, mut n: u32) -> ModInt {
        let mut t = ModInt::one();
        let mut s = self;
        while n > 0 {
            if n & 1 == 1 {
                t *= s;
            }
            s *= s;
            n >>= 1;
        }
        t
    }
    pub fn inv(self) -> ModInt {
        assert!(self.0 > 0);
        self.pow(MOD - 2)
    }
}
// ---------- end ModInt ----------

use proconio::*;

fn run() {
    input! {
        n: usize,
        mut a: usize,
        mut b: usize,
    }
    if a > b {
        std::mem::swap(&mut a, &mut b);
    }
    let (a, b) = (a, b);
    let mut memo = vec![ModInt::zero(); b + 1];
    memo[1] = ModInt::one();
    for i in 1..b {
        let v = memo[i];
        memo[i + 1] += v;
        for j in (a + 1).. {
            if i + j > b {
                break;
            }
            memo[i + j] += v;
        }
    }
    let mut dp0 = vec![ModInt::zero(); 2 * n + 1];
    let mut dp1 = vec![ModInt::zero(); 2 * n + 1];
    for i in 1..a {
        dp0[i] = ModInt::one();
    }
    for i in 1..b {
        dp1[i] = memo[i + 1];
    }
    for i in 1..=n {
        let v = dp0[i];
        for j in 1..b {
            dp1[i + j] += v * memo[j];
        }
        let v = dp1[i];
        for j in 1..a {
            dp0[i + j] += v;
        }
    }
    let mut ans = ModInt(2).pow(n as u32) - (dp0[n] + dp1[n]);
    for i in a..b {
        for j in 1.. {
            if i + j >= b {
                break;
            }
            ans -= dp0[n - i - j] * memo[j];
        }
    }
    println!("{}", ans);
}

fn main() {
    run();
}
