    for (int i = n; i < 2 * n; i++) d1.insert(i);
    for (int i = 2 * n; i < 3 * n; i++) d2.insert(i);

    for (int i = 0; i < s.size(); i++)
    {
        int x = last_w[i];
        vector<int> q = {x, x + n, x + 2 * n};
        for (auto e : q) if (d2.find(e) != d2.end()) {w.push_back(e); break;}
        if (s[i] == 'S')
        {
