            int f0, f1, f2;
            for (auto e : q) if (d0.find(e) != d0.end()) {f0 = e; break;}
            for (auto e : q) if (d1.find(e) != d1.end()) {f1 = e; break;}
            for (auto e : q) if (d2.find(e) != d2.end()) {f2 = e; break;}
            d0.erase(f0); d1.insert(f0);
            d1.erase(f1); d2.insert(f1);
            d2.erase(f2); d0.insert(f2);
        }
    }

    for (int i = 0; i < 3 * n; i++)
    {
        p[i] = last_p[i % n];
    }
    for (auto e : d1) p[e] += n;
    for (auto e : d2) p[e] += n + n;

    return {p, w};
}

