#include <bits/stdc++.h>

using namespace std;

int n;
string str;
int val[2000010][3], ch[2000010][3], tot = 1;
bool laz[2000010];

void build()
{
	for (int i = 1; i <= n + 1; i++) tot *= 3;
	tot = (tot - 1) / 2;
	for (int i = 1; i <= tot; i++)
		if (i * 3 <= tot)
		{
			ch[i][0] = i * 3 - 1, ch[i][1] = i * 3, ch[i][2] = i * 3 + 1;
			val[i][0] = 0, val[i][1] = 1, val[i][2] = 2;
			laz[i] = 0;
		}
}

void pushdown(int p)
{
	if (laz[p])
	{
		int a, b;
		for (int i = 0; i < 3; i++)
		{
			if (val[p][i] == 1) a = i;
			if (val[p][i] == 2) b = i;
		}
		swap(val[p][a], val[p][b]);
		for (int i = 0; i < 3; i++)
			laz[ch[p][i]] ^= 1;
		laz[p] = 0;
	}
}

void add(int p)
{
	if (p * 3 > tot) return;
	pushdown(p);
	int pos = 0;
	for (int i = 0; i < 3; i++)
	{
		val[p][i]++;
		if (val[p][i] == 3)
		{
			val[p][i] = 0;
			pos = ch[p][i];
		}
	}
	add(pos);
}

int arr[2000010], cnt;
int rev[2000010];

void dfs(int p, int cur, int v)
{
	if (p * 3 > tot)
	{
		arr[cnt++] = cur;
		return;
	}
	pushdown(p); 
	for (int i = 0; i < 3; i++)
		dfs(ch[p][i], cur + v * val[p][i], v * 3);
}

int main()
{
	ios::sync_with_stdio(false);
	cin >> n >> str;
	build();
	for (int i = 0; i < str.size(); i++)
	{
		if (str[i] == 'R') add(1);
		else laz[1] ^= 1;
	}
	dfs(1, 0, 1);
	for (int i = 1; i < cnt; i++)
		rev[i] = rev[i / 3] / 3 + (cnt / 3) * (i % 3);
	for (int i = 0; i < cnt; i++)
		if (i < rev[i])
			swap(arr[i], arr[rev[i]]);
	for (int i = 0; i < cnt; i++)
		cout << arr[i] << ' ';
	cout << endl; 
	return 0;
}

