ll N;
string T;

pair<vll, vll> solve(ll n){
  if(n==0){
    vll P0 = {0};
    vll W0(T.size()+1, 0);
    return make_pair(P0, W0);
  }
  auto PW_prev = solve(n-1);
  auto P_old = PW_prev.first;
  auto W_old = PW_prev.second;

  /*
  for(auto ans : PW_prev.first){
    printf("%lld ", ans);
  }
  printf("\n");

  for(auto tmp : PW_prev.second){
    printf("%lld ", tmp);
  }
  printf("\n\n");
  */

  vll P_new(P_old.size()*3);
  vll D_new(P_old.size()*3);
  vll W_new(W_old.size());

  vll S_ahead_count(P_old.size()*3);
  ll S_count = 0;

  REP(i,D_new.size()) D_new[i] = i/P_old.size();

  REP(i,T.size()){
    auto W = W_old[i];
    //debug_print(W);
    auto c = T[i];
    for(auto j=W; j<P_new.size(); j+=P_old.size()){
      if((S_count-S_ahead_count[j])%2==1){
        S_ahead_count[j] = S_count;
        D_new[j] = (3-D_new[j])%3;
      }
      if(D_new[j]==2) W_new[i] = j;
    }
    if(c=='S'){
      S_count++;
    }else{
      for(auto j=W; j<P_new.size(); j+=P_old.size()){
        D_new[j] = (D_new[j]+1)%3;
      }
    }

    //debug_print(i, W, c);
    REP(k,P_new.size()){
      ll D_tmp = D_new[k];
      if((S_count-S_ahead_count[k])%2==1){
        D_tmp = (3-D_new[k])%3;
      }
      P_new[k] = P_old[k%P_old.size()] + P_old.size()*D_tmp;
      //printf("%lld ", D_tmp);
    }

    /*
    printf("\n");
    debug_print(i);
    */
  }

  return make_pair(P_new, W_new);
}

