using namespace std;
const int N=600005;
int n,rt,cnt,ind,pw[N],ch[N][3],tg[N],val[N],ans[N];char s[N];
void bud(int&k1,int k2,int k3){
	k1=++cnt;
	if(k2==n){val[k1]=k3;return;}
	rep(i,0,2)bud(ch[k1][i],k2+1,k3+pw[k2]*i);
}
void pst(int k1){
	if(!ch[k1][0])return;
	swap(ch[k1][1],ch[k1][2]);
	tg[k1]^=1;
}
void psd(int k1){
	if(!ch[k1][0])return;
	if(tg[k1]){
		pst(ch[k1][0]);
		pst(ch[k1][1]);
		pst(ch[k1][2]);
		tg[k1]^=1;
	}
}
void go(int k1){
	if(!ch[k1][0])return;
	psd(k1);
	int t=ch[k1][2];ch[k1][2]=ch[k1][1],ch[k1][1]=ch[k1][0],ch[k1][0]=t;
	go(ch[k1][0]);
}
void dfs(int k1,int k2,int k3){
	if(k2==n){ans[val[k1]]=k3;return;}
	psd(k1);
	rep(i,0,2)dfs(ch[k1][i],k2+1,k3+pw[k2]*i);
}
int main(){
	scanf("%d%s",&n,s);
	pw[0]=1;rep(i,1,n)pw[i]=pw[i-1]*3;
	bud(rt,0,0);
	for(int i=0;s[i];++i){
		if(s[i]=='S')pst(rt);
		else go(rt);
	}
	dfs(rt,0,0);
	rep(i,0,pw[n]-1)printf("%d ",ans[i]);
	return 0;
}

