using namespace std;
int n,p3[13],tot;
string t;
int son[797170][3],laz[797170],leaf[797170],p[797170];
void push_down(int x){
	if(leaf[x]){laz[x]=0;return;}
	if(laz[x]){
		laz[x]=0;
		laz[son[x][0]]^=1;
		laz[son[x][1]]^=1;
		laz[son[x][2]]^=1;
		swap(son[x][1],son[x][2]);
	}
	return;
} 
void update(int x){
	if(leaf[x]) return;
	push_down(x);
	update(son[x][2]);
	int tmp=son[x][0];
	son[x][0]=son[x][2];
	son[x][2]=son[x][1];
	son[x][1]=tmp;
	return;
}
void build(int x,int level){
	if(level==n){leaf[x]=1;return;}
	son[x][0]=++tot;build(son[x][0],level+1);
	son[x][1]=++tot;build(son[x][1],level+1);
	son[x][2]=++tot;build(son[x][2],level+1);
	return;
}
void dfs(int x,int cu,int level){
	if(leaf[x]){p[x-(p3[n]-1)/2]=cu;return;}
	push_down(x);
	dfs(son[x][0],cu+p3[level]*0,level+1);
	dfs(son[x][1],cu+p3[level]*1,level+1);
	dfs(son[x][2],cu+p3[level]*2,level+1);
	return;
}
