  vector<int> next = {0, 2, 1};
  vector<int> pw(N + 1);
  pw[0] = 1;
  for (int i = 1; i <= N; ++i) pw[i] = pw[i - 1] * 3;
  int M = pw[N];

  vector<vector<int>> f(N), sw(N);
  for (int i = 0; i < N; ++i) {
    f[i].resize(pw[i + 1]);
    sw[i].resize(pw[i + 1]);
    for (int j = 0; j < pw[i + 1]; ++j) {
      f[i][j] = j / pw[i];
    }
  }

  int swapsCnt = 0;
  for (char c : T) {
    if (c == 'S') {
      swapsCnt ^= 1;
    } else {
      int suf = 0;
      for (int k = 0; k < N; ++k) {
        int v = -1;
        for (int d = 0; d < 3; ++d) {
          int x = pw[k] * d + suf;
          if (sw[k][x] != swapsCnt) {
            f[k][x] = next[f[k][x]];
            sw[k][x] ^= 1;
          }
          f[k][x]++;
          if (f[k][x] == 3) {
            f[k][x] = 0;
            v = d;
          }
        }
        assert(v != -1);
        suf += pw[k] * v;
      }
    }
  }

  for (int x = 0; x < M; ++x) {
    int ans = 0, c = x;
    for (int k = N - 1; k >= 0; --k) {
      if (sw[k][c] != swapsCnt) {
        f[k][c] = next[f[k][c]];
        sw[k][c] ^= 1;
      }
      ans += pw[k] * f[k][c];
      c %= pw[k];
    }
    cout << ans << ' ';
  }
  cout << '\n';
  return 0;
}

