#include <bits/stdc++.h>
using namespace std;

int main() {
    cin.tie(nullptr);
    ios::sync_with_stdio(false);

    int N; cin >> N;
    string T; cin >> T;

    vector<int> C = {1};
    for (int i = 0; i < N; i++) {
        C.emplace_back(C.back() * 3);
    }
    vector<vector<int>> V(N);
    vector<int> L1(N), L2(N);

    for (int i = 0; i < N; i++) {
        V[i].resize(C[i+1]);
        for (int j = 0; j < C[i+1]; j++) {
            V[i][j] = j / C[i];
        }
        L1[i] = (C[i+1] - 1) / 2;
        L2[i] = C[i+1] - 1;
    }

    auto upd = [&](auto &L, int n) {
        for (int i = 0; i < N; i++) {
            int X = i == 0 ? 0 : L[i-1];
            for (int j = 0; j < 3; j++) {
                int Y = (X + j * C[i]) % C[i+1];
                if (V[i][Y] == n) {
                    L[i] = Y; break;
                }
            }
        }
    };
    bool sw = false;
    auto S = [&]() { sw = !sw; };
    auto R = [&]() {
        if (!sw) {
            for (int i = 0; i < N; i++) {
                for (int j = 0; j < 3; j++) {
                    int X = (L2[i] + j * C[i]) % C[i+1];
                    V[i][X] = (V[i][X] + 1) % 3;
                }
            }
        } else {
            for (int i = 0; i < N; i++) {
                for (int j = 0; j < 3; j++) {
                    int X = (L1[i] + j * C[i]) % C[i+1];
                    V[i][X] = (V[i][X] + 2) % 3;
                }
            }
        }
        upd(L1, 1), upd(L2, 2);
    };

    for (auto &c: T) {
        if (c == 'S') S();
        else R();
    }

    if (sw) {
        for (auto &v: V) {
            for (auto &x: v) {
                x = (3 - x) % 3;
            }
        }
    }

    for (int i = 0; i < C[N]; i++) {
        long long x = 0;
        for (int j = 0; j < N; j++) {
            x += C[j] * V[j][i%C[j+1]];
        }
        if (i) cout << " ";
        cout << x;
    }
    cout << "\n";

    return 0;
}

#include <bits/stdc++.h>

using namespace std;

const int N = 6e5 + 5;

char s[N];
int p[N], from[N], last[N];
int dig[N];

int main() {
  int n;
  scanf("%d", &n);
  scanf("%s", s);
  int len = strlen(s);
  int pw = 1;
  fill(last, last+len, 0);
  for (int i = 0; i < n; ++i) {
    bool swapped = false;
    for (int j = 0; j < pw; ++j) {
      dig[j] = 0;
      dig[pw + j] = 1;
      dig[pw * 2 + j] = 2;
    }
    for (int j = 0; j < len; ++j) {
      if (s[j] == 'S') {
        swapped = !swapped;
      } else {
        int add = 0;
        for (int k = 0; k < 3; ++k) {
          int now = k * pw + last[j];
          if (swapped) {
            if (dig[now] == 1)
              add = k;
            if (dig[now] == 0)
              dig[now] = 2;
            else if (dig[now] == 1)
              dig[now] = 0;
            else
              dig[now] = 1;
          } else {
            if (dig[now] == 2)
              add = k;
            dig[now] = (dig[now] + 1) % 3;
          }
        }
        last[j] += add * pw;
      }
    }
    for (int j = 0; j < pw; ++j) {
      int now = p[j];
      for (int k = 0; k < 3; ++k) {
        int cur = j + k * pw;
        if (swapped && dig[cur] != 0) {
          dig[cur] = 3 - dig[cur];
        }
        p[cur] = now + dig[cur] * pw;
      }
    }
    pw *= 3;
  }
  for (int i = 0; i < pw; ++i) {
    printf("%d%c", p[i], i+1 == pw ? '\n' : ' ');
  }
  return 0;
}

