const int N = 1e7 + 5;
int _w;

int son[N][3] , nidx , val[N] , n , m , rev[N] , bin[13] , rmp[N];
char str[N];

void pre( int u , int now , int dep ) {
  if( dep == n ) {
    val[u] = now;
    return;
  }
  for( int i = 0 ; i < 3 ; ++i ) {
    son[u][i] = ++nidx;
    pre( son[u][i] , now + i * bin[dep] , dep + 1 );
  }
}

void dfs( int u , int dep ) {
  if( dep == n ) return;
  if( rev[u] ) {
    swap( son[u][1] , son[u][2] );
    rev[son[u][1]] ^= 1;
    rev[son[u][2]] ^= 1;
    rev[son[u][0]] ^= 1;
    rev[u] = 0;
  }
  dfs( son[u][2] , dep + 1 );
  int t = son[u][2];
  son[u][2] = son[u][1]; son[u][1] = son[u][0];
  son[u][0] = t;
}

void ans( int u , int now , int dep ) {
  if( dep == n ) {
    rmp[val[u]] = now;
    return;
  }
  if( rev[u] ) {
    swap( son[u][1] , son[u][2] );
    rev[son[u][2]] ^= 1;
    rev[son[u][1]] ^= 1;
    rev[son[u][0]] ^= 1;
    rev[u] = 0;
  }
  for( int i = 0 ; i < 3 ; ++i )
    ans( son[u][i] , now + i * bin[dep] , dep + 1 );
}

int main( void ) {
