  _w = scanf("%s",str+1);
  m = strlen( str + 1 );
  for( int i = 1 ; i <= m ; ++i ) 
    if( str[i] == 'S' )
      rev[0] ^= 1;
    else 
      dfs( 0 , 0 );
  ans( 0 , 0 , 0 );
  for( int i = 0 ; i < bin[n] ; ++i )
    printf("%d ",rmp[i]);
  return 0;
}

