        // conduct salsa
        D[n][indx] = 3-D[n][indx];
    }
    // update the lastUpdated index
    lastUpdated[n][indx] = cntS;
}

int pow3[16];

int main(){
    scanf("%d", &N);
    scanf(" %s", T);

    int M = strlen(T);

    pow3[0] = 1;
    for(int i = 1; i <= N; i ++){
        pow3[i] = pow3[i-1]*3;
    }

    //memset(W, 0, sizeof(W));
    int tempNum = 1;
    for(int n = 1; n <= N; n ++){
        //printf("ok");
        for(int j = 0; j < tempNum; j ++){
            D[n][j] = 0;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        for(int j = tempNum; j < (tempNum<<1); j ++){
            D[n][j] = 1;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        for(int j = (tempNum<<1); j < 3*tempNum; j ++){
            D[n][j] = 2;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        W[n][0] = pow3[n]-1;
        tempNum *= 3;
    }

    //memset(lastUpdated, 0, sizeof(lastUpdated));
    int cntS2 = 0;
    tempNum = 1;
    int tempNum2 = 3;
    for(int n = 1; n <= N; n ++){
        int cntS = 0;
        for(int i = 0; i < M; i ++){
            int indx0 = W[n-1][i];
            int indx1 = W[n-1][i]+tempNum;
            int indx2 = W[n-1][i]+(tempNum<<1);

            if(indx1 >= tempNum2){indx1 -= tempNum2;}
            if(indx2 >= tempNum2){indx2 -= tempNum2;}

            update(n, indx0, cntS+(T[i]=='S'));
            update(n, indx1, cntS+(T[i]=='S'));
            update(n, indx2, cntS+(T[i]=='S'));
            if(D[n][indx0] == 2){
                W[n][i] = indx0;
            }else if(D[n][indx1] == 2){
                W[n][i] = indx1;
            }else{
                W[n][i] = indx2;
            }

            if(T[i] == 'R'){
                if( (++D[n][indx0]) == 3){D[n][indx0] = 0;}
                if( (++D[n][indx1]) == 3){D[n][indx1] = 0;}
                if( (++D[n][indx2]) == 3){D[n][indx2] = 0;}
                lastUpdated[n][indx0] = cntS;
                lastUpdated[n][indx1] = cntS;
                lastUpdated[n][indx2] = cntS;
            }else{
                cntS ++;
            }

            //printf("W[%d][%d]=%d\n", n, i+1, W[n][i+1]);
        }

        tempNum *= 3;
        tempNum2 *= 3;

        cntS2 = cntS;
    }

    for(int n = 1; n <= N; n ++){
        for(int i = 0; i < pow3[n]; i ++){
            update(n, i, cntS2);
        }

        int cnt = 0;
        for(int i = 0; i < pow3[N]; i ++){
            res[i] += D[n][cnt++]*pow3[n-1];
            if(cnt == pow3[n]){cnt = 0;}
        }
    }

    for(int i = 0; i < pow3[N]; i ++){
        /*int temp = 0;
        for(int n = 1; n <= N; n ++){
            temp += D[n][i%pow3[n]]*pow3[n-1];
        }*/
        //putInt(temp);
        putInt(res[i]);
    }

    return 0;
}

