class people {
public:
	people() { n = 0; }
	~people() {
		if (n != 0) {
			delete[]trip; delete[]bit;
		}
	}
	int pos;
	int n;
	int num;
	int* trip;
	int* bit;
	void init(int _n, int _pos) {
		n = _n;
		num = pow(3, n);
		trip = new int[n + 1];
		bit = new int[n + 1];
		pos = _pos;
		for (int i = 0; i < n; i++) {
			bit[i] = pow(3, i);
		}
		c10to3();
	}
	void c3to10() {
		int v = 0;
		for (int i = 0; i < n; i++) {
			v += bit[i] * trip[i];
		}
		pos = v % num;
	}
	void c10to3() {
		int v = pos;
		for (int i = 0; i < n; i++) {
			trip[i] = v % 3;
			v = (v - trip[i]) / 3;
		}
	}
	void rumba() {
		pos = (pos + 1) % num;
		c10to3();
	}
	void sarusa() {
		for (int i = 0; i < n; i++) {
			if (trip[i] == 1)trip[i] = 2;
			else if (trip[i] == 2)trip[i] = 1;
		}
		c3to10();
	}
};

