int build(int dep,int l)
{
	int x=++cnt;
	if(dep==n){
		id[x]=l;
		return x;
	}
	int t=pw3[dep];
	ch[x][0]=build(dep+1,l);
	ch[x][1]=build(dep+1,l+t);
	ch[x][2]=build(dep+1,l+t+t);
	return x;
}
void upd(int x)
{
	rev[x]^=1;
	swap(ch[x][1],ch[x][2]);
}
void pushdo(int x)
{
	if(rev[x]){
		upd(ch[x][0]);
		upd(ch[x][1]);
		upd(ch[x][2]);
		rev[x]=0;
	}
}
void sft(int x,int d)
{
	if(d==n) return;
	pushdo(x);
	int tmp=ch[x][0];
	ch[x][0]=ch[x][2];
	ch[x][2]=ch[x][1];
	ch[x][1]=tmp;
	sft(ch[x][0],d+1);
}
