namespace Trie {
  int tot = 0, son[N][3], id[N];
  bool rev[N];
  inline void pushnow(int p) {
    rev[p] ^= 1;
    swap(son[p][1], son[p][2]);
  }
  inline void pushdown(int p) {
    if (rev[p]) {
      rev[p] ^= 1;
      if (son[p][0]) pushnow(son[p][0]);
      if (son[p][1]) pushnow(son[p][1]);
      if (son[p][2]) pushnow(son[p][2]);
    }
  }
  inline void build(int &p, int dep, int ss = 0, int pw = 1) {
    p = ++tot;
    if (dep == n) {
      id[p] = ss;
      return;
    }
    for (ri i = 0; i < 3; ++i) build(son[p][i], dep + 1, ss + pw * i, pw * 3);
  }
  inline void upd(int p, int dep) {
    if (dep == n) return;
    pushdown(p);
    swap(son[p][1], son[p][2]);
    swap(son[p][0], son[p][1]);
    upd(son[p][0], dep + 1);
  }
  inline void dfs(int p, int dep, int ss = 0, int pw = 1) {
    if (dep == n) {
      res[id[p]] = ss;
      return;
    }
    pushdown(p);
    for (ri i = 0; i < 3; ++i) dfs(son[p][i], dep + 1, ss + pw * i, pw * 3);
  }
}
