#include<bits/stdc++.h>
using namespace std;

int tag[531441];

void solve(int n,const string& T,vector<int> &perm,vector<int> &pos){
	if(n==0){
		perm={0};
		pos=vector<int>(T.size(),0);
		return;
	}
	vector<int> pe,po;
	solve(n-1,T,pe,po);
	pos.resize(T.size());
	perm.resize(pe.size()*3);
	vector<int> msb;
	for(int i=0;i<3;i++)
		for(int j=0;j<pe.size();j++)
			msb.emplace_back(i);
	memset(tag,0,sizeof tag);
	int t=0;
	for(int i=0;i<T.size();i++){
		for(int j=po[i];j<msb.size();j+=pe.size()){
			if(tag[j]%2!=t%2)	if(msb[j])	msb[j]=3-msb[j];
			tag[j]=t;
			if(msb[j]==2)	pos[i]=j;
		}
		if(T[i]=='S')	t++;
		else{
			for(int j=po[i];j<msb.size();j+=pe.size())
				msb[j]=(msb[j]+1)%3;
		}
	}
	for(int i=0;i<perm.size();i++){
		if(tag[i]%2!=t%2)	if(msb[i])	msb[i]=3-msb[i];
		perm[i]=msb[i]*pe.size()+pe[i%pe.size()];
	}
}

