using namespace std;
typedef long long ll;

int pow(int a, int n) {
	if (n <= 0) return 0;
	if (n == 1) return a;
	return(pow(a, n - 1) * a);
}

int tri_rev(int t, int n) {
	if (t / n == 0) return t;
	int x = (t % (n * 3)) / n;
	int y = (x == 1) ? 2 : ((x == 2) ? 1 : 0);
	return tri_rev(t - x * n + y * n, n * 3);
}

void readRS(const char* p, vector<int> &RS) {
	RS.clear();
	int sw = 0;
	int c = 1;
	bool f = true;
	while (f) { // R = 82 = , S = 83 = 3
		if (*p == sw) {
			c++;
		}
		else {
			if (sw == 83) {
				if (c % 2 == 1) {
					RS.push_back(-1);
				}
			}
			else if (sw == 82) {
				for (int i = 0; i < c; i++) { RS.push_back(1); }
			}
			c = 1;
			sw = *p;
		}
		if (*p == '\0') break;
		p++;
	}

}

int main() {
	int N;
	cin >> N;

	// readRS
	string s;
	cin >> s;
	vector<int> RS(200001,0); // 1 = , -1 = 3
	readRS(s.c_str(), RS);

	// swap set	
	vector<int> SW(pow(3, N), 0);
	for (int i = 0; i < pow(3, N); i++){
		if(SW[i] == 0){
			int j = tri_rev(i, 1);
			SW[i] = j;
			SW[j] = i;
		}
	}


	vector<int> P0(1,0); // lvP0[i] = ilv=0P0[0]=0
	vector<int> W0(RS.size() + 1, 0); // RSIDlv = 0W0[k] = 0 for all k
								 // W0[0] = RSID = 3^lv -1
								 // W0[RS.size()-1] = RSID
	for (int lv = 1; lv <= N; lv++) {
		// 
		// 3^lv 
		int M = pow(3, lv); // 
		int m = M / 3; // lvm
		vector<int> P1(M, 0); // 
		vector<int> W1(RS.size() + 1, 0); // kID
		map<int,int> D0, D1, D2; // digitkDxiddigitx

		// D
		for (int i = 0; i < m; i++) {
			D0.insert(pair<int, int>(i, i));
			D1.insert(pair<int, int>(i, i + m));
			D2.insert(pair<int, int>(i, i + 2 * m));
		}

		// D, W1
		W1[0] = M - 1;
		for (int k = 0; k < RS.size(); k++) {
			// D
			if (RS[k] == -1) {
				// 3: D1 -> 2, 2 -> 1
				D1.swap(D2);
			}
			else {
				//  
				int i = D2[W0[k]];
				D2[W0[k]] = D1[W0[k]];
				D1[W0[k]] = D0[W0[k]];
				D0[W0[k]] = i;
			}
			// W1[k+1] W1[k+1]2,W0[k+1]
			W1[k + 1] = D2[W0[k + 1]];
		}
		// 
		for (auto it = D0.begin(); it != D0.end(); it++) {
			P1[it->second] = P0[it->first];
		}
		for (auto it = D1.begin(); it != D1.end(); it++) {
			P1[it->second] = P0[it->first] + m;
		}
		for (auto it = D2.begin(); it != D2.end(); it++) {
			P1[it->second] = P0[it->first] + 2 * m;
		}

		// 
		P0.swap(P1);
		W0.swap(W1);
	}

	// out
	for (int i = 0; i < pow(3, N); i++) {
		cout << P0[i] << " ";
	}
	cout << endl;
	return(0);
}

