	for(int i=0;i<n;i++){
		set<int> se[3];
		for(int j=0;j<3;j++){
			for(int k=j*san[i];k<(j+1)*san[i];k++)se[j].insert(k);
		}
		las[0]+=2*san[i];
		for(int j=0;j<m;j++){
