					else if(!sumi[1]&&se[k].count(mae+san[i]))tmp[k]=mae+san[i],sumi[1]=1;
					else tmp[k]=mae+san[i]*2,sumi[2]=1;
				}
				for(int k=0;k<3;k++){
					se[k].erase(tmp[k]);
					se[(k+1)%3].insert(tmp[k]);
				}
			}
