	maxn = B = 1;
	for(int i = 0; i < n; i++) maxn *= 3;
	for(int i = 0; i < n/2; i++) B *= 3;
	C = maxn/B;
	for(int i = 0; i < maxn; i++) {
		f[i] = rev(i);
		small[i%B].push_back(i);
		id[i] = i/B;
	}

	for(auto i : t) {
		if(i == 'R') add();
		else flip();
	}
	for(int i = 0; i < B; i++)
		for(auto j : small[i]) p[j] += (B+i-s)%B;
	for(int i = 0; i < maxn; i++) {
		if(lup[i] != u) {
			if((lup[i]^u)&1) id[i] = f[id[i]];
			lup[i] = u;
		}
		p[i] += B*id[i];
	}
	for(int i = 0; i < maxn; i++) cout << p[i] << " ";
}

