const int N = 15;
int pow3[N] = {};

vector<int> rec(int n, string const& T) {
    if (n == 0) return {0};
    vector<int> ret(pow3[n]);
    rep (i,3) {
        string nT;
        int x = i;
        for (char c : T) {
            if (c == 'S') {
                if (nT.size() && nT.back() == 'S') {
                    nT.pop_back();
                } else {
                    nT += c;
                }
                if (x > 0) x = 3-x;
            } else {
                x++;
                if (x == 3) {
                    x = 0;
                    nT += c;
                }
            }
        }
        auto res = rec(n-1,nT);
        rep (j,pow3[n-1]) ret[3*j+i] = 3*res[j]+x;
    }
    return ret;
};

int32_t main() {
    pow3[0] = 1;
    rep (i,N-1) pow3[i+1] = 3*pow3[i];

    int n; cin >> n;
    string T; cin >> T;
    cout << rec(n,T) << endl;
}

