void rumba(){
	for(int i=0;i<pow(3,n);i++){
		int j=0;
		pos[i][j]++;
		while(pos[i][j]==3 && j!=n){
			pos[i][j]=0;
			pos[i][j+1]++;
			j++;
		}

	}
}

