LL fp(LL a, LL n, LL mod = MOD)
{
    if(n < 0) a = fp(a, mod - 2, mod), n = -n;
    LL res = 1; for(; n; n >>= 1, a = a * a % mod) if(n & 1) res = res * a % mod; return res;
}

struct Mint
{
    int x;
    Mint() {x = 0;}
    Mint(int _x): x(_x) {if(x < 0 || x >= MOD) x = (x % MOD + MOD) % MOD;}
    Mint(LL _x): x(_x) {if(x < 0 || x >= MOD) x = (x % MOD + MOD) % MOD;}
    Mint operator - () const{
        return Mint(MOD - x);
    }
    Mint operator + (const Mint &rhs) const{
        return Mint(x + rhs.x >= MOD ? x + rhs.x - MOD : x + rhs.x);
    }
    Mint operator - (const Mint &rhs) const{
        return Mint(x - rhs.x < 0 ? x - rhs.x + MOD : x - rhs.x);
    }
    Mint operator * (const Mint &rhs) const{
        return Mint((LL)x * rhs.x % MOD);
    }
    Mint operator / (const Mint &rhs) const{
        return Mint(x * fp(rhs.x, -1) % MOD);
    }
    Mint& operator += (const Mint &rhs) {
        x += rhs.x; if(x >= MOD) x -= MOD; return *this;
    }
    Mint& operator *= (const Mint &rhs) {
        x = ((LL)x * rhs.x) % MOD; return *this;
    }
    bool operator == (const Mint &rhs) const{
        return x == rhs.x;
    }
    bool operator != (const Mint &rhs) const{
        return x != rhs.x;
    }
    friend ostream& operator << (ostream &out, const Mint &rhs){
        return out << rhs.x;
    }
    friend istream& operator >> (istream &in, Mint &rhs){
        return in >> rhs.x;
    }
};


const int maxn = (int) 2e5 + 20;
const int maxm = (int) 1e6 + 20;

struct Node
{
    int f;
    int idx;
    Node *p[3];
    Node() {f = 0; idx = -1; memset(p, 0, sizeof(p));}

    void rev() {f ^= 1; swap(p[1], p[2]);}
    void push()
    {
        if(f)
        {
            f = 0;
            for(int i = 0; i < 3; i++) p[i]->rev();
        }
    }
};

void work()
{
    int n;
    cin >>n;
    string s;
    cin >>s;

    vector<int> po(n + 1);
    po[0] = 1;
    for(int i = 1; i <= n; i++) po[i] = po[i - 1] * 3;

    Node *rt = new Node();
    for(int i = 0; i < po[n]; i++)
    {
        Node *ptr = rt;
        for(int j = 0; j < n; j++)
        {
            int w = i / po[j] % 3;
            if(!ptr->p[w]) ptr->p[w] = new Node();
            ptr = ptr->p[w];
        }
        ptr->idx = i;
    }

    for(auto ch : s)
    {
        if(ch == 'S') rt->rev();
        else
        {
            Node *ptr = rt;
            for(int j = 0; j < n; j++)
            {
                ptr->push();
                Node *t = ptr->p[2];
                ptr->p[2] = ptr->p[1];
                ptr->p[1] = ptr->p[0];
                ptr->p[0] = t;

                ptr = ptr->p[0];
            }
        }

//        cout <<rt->p[0]->idx <<" " <<rt->p[1]->idx <<" " <<rt->p[2]->idx <<endl;
    }

    vector<int> p(po[n]);

    for(int i = 0; i < po[n]; i++)
    {
        Node *ptr = rt;
        for(int j = 0; j < n; j++)
        {
            int w = i / po[j] % 3;
            ptr->push();
            ptr = ptr->p[w];
        }
        p[ptr->idx] = i;
    }
    for(int i = 0; i < po[n]; i++)
        cout <<p[i] <<" \n"[i == po[n] - 1];
}

