int flip(int x)
{
	int ans=0; int cur=1;
	while(x>0)
	{
		int d=x%3;
		if(d==1) d=2;
		else if(d==2) d=1;
		ans+=cur*d;
		x/=3;
		cur*=3;
	}
	return ans;
}

