vector<int> calc(int n) {
	if (n == 0) {
		vector<int> ret(nmove + 1);
		REP(i, nmove) ret[i] = i + 1; ret[nmove] = 0;
		return ret;
	} else {
		vector<int> sub = calc(n - 1);
		int partsize = SZ(sub) - nmove;
		vector<int> nxt(3 * (nmove + partsize));
		REP(atpart, 3) REPSZ(atpos, sub) {
			int topos = sub[atpos], topart;
			if (topos >= nmove) topart = atpart; else if (swapcnt[topos] % 2 == 0) topart = atpart + 1; else topart = atpart == 0 ? 2 : atpart == 1 ? 3 : 1;
			nxt[atpart * (nmove + partsize) + atpos] = topart * (nmove + partsize) + topos;
		}
		//printf("nxt:"); REPSZ(i, nxt) printf(" %d", nxt[i]); puts("");
		vector<int> ret(nmove + 3 * partsize);
		REP(i, nmove + 3 * partsize) {
			int atpart = i < nmove ? 0 : (i - nmove) / partsize, atpos = i < nmove ? i : nmove + (i - nmove) % partsize;
			while (true) {
				int to = nxt[atpart * (nmove + partsize) + atpos], topart = to / (nmove + partsize), topos = to % (nmove + partsize);
				//printf("%d: (%d,%d) -> (%d,%d)\n", i, atpart, atpos, topart, topos);
				if (topos >= nmove) {
					assert(topart == atpart);
					ret[i] = nmove + (topart == 0 || nswap % 2 == 0 ? atpart : 3 - atpart) * partsize + topos - nmove;
					break;
				} else if (topart >= 3) {
					ret[i] = topos;
					break;
				} else {
					atpart = topart, atpos = topos;
				}
			}
		}
		return ret;
	}
}

void solve() {
	nmove = nswap = 0; REP(i, slen) if (s[i] == 'S') ++nswap; else swapcnt[nmove] = nswap, ++nmove;
	vector<int> trans = calc(n);
	//printf("trans:"); REPSZ(i, trans) printf(" %d", trans[i]); puts("");
	int totsize = SZ(trans) - nmove;
	ans=vector<int>(totsize);
	REP(i, totsize) {
		int atpos = nmove + i;
		while (true) {
			int topos = trans[atpos];
			if (topos >= nmove) {
				ans[i] = topos - nmove;
				break;
			} else {
				atpos = topos;
			}
		}
	}
	//printf("ans:"); REPSZ(i, ans) printf(" %d", ans[i]); puts("");
}

void run() {
	scanf("%d", &n);
	scanf("%s", s); slen = strlen(s);
	solve();
	REPSZ(i, ans) { if (i != 0) printf(" "); printf("%d", ans[i]); } puts("");
}

