int cng(int x){
  int y=0, i=0, z;
  while(x > 0){
    z = x%3;
    if(z==1){
      z=2;
    }else if(z==2){
      z=1;
    }
    y += z*pow(10,i);
    x = x/3;
    i++; 
  }
  string temp=to_string(y);
  int lg=temp.length();
  int ans=0;
  for(int i=0;i<lg;i++){
    ans+=(temp.at(i)-'0')*pow(3,lg-i-1);
  }
  return ans;
}


