using namespace std;

int n;
char T[200005];

vector<int> inc[12][550000];
int ans[12][550000];
int psum[200005];

int pwr3[15];

int main()
{
    pwr3[0] = 1;
    for (int i = 1; i < 15; i++) pwr3[i] = 3 * pwr3[i-1];
    scanf("%d", &n);
    scanf("%s", T + 1);
    int tsiz = 0;
    for (int i = 1; T[i]; i++) {
        tsiz = i;
        psum[i] = psum[i-1] + (T[i] == 'S' ? 1 : 0);
    }
    for (int s = 0; s < 3; s++) {
        int cur = s;
        for (int i = 1; T[i]; i++) {
            if (T[i] == 'S') {
                if (cur) cur = 3 - cur;
            } else {
                cur++;
                if (cur == 3) {
                    cur = 0; inc[0][s].push_back(i);
                }
            }
        }
        ans[0][s] = cur;
    }
    for (int dig = 1; dig < n; dig++) {
        for (int high = 0; high < 3; high++) {
            for (int low = 0; low < pwr3[dig]; low++) {
                int num = high * pwr3[dig] + low;
                int lastinc = 0;
                int curhigh = high;
                for (int i = 0; i < inc[dig-1][low].size(); i++) {
                    int curinc = inc[dig-1][low][i];
                    if ((psum[curinc] - psum[lastinc]) % 2) {
                        if (curhigh) curhigh = 3 - curhigh;
                    }
                    curhigh++;
                    if (curhigh == 3) {
                        curhigh = 0; inc[dig][num].push_back(curinc);
                    }
                    lastinc = curinc;
                }
                if ((psum[tsiz] - psum[lastinc]) % 2) {
                    if (curhigh) curhigh = 3 - curhigh;
                }
                ans[dig][num] = curhigh * pwr3[dig] + ans[dig-1][low];
            }
        }
    }
    for (int i = 0; i < pwr3[n]; i++) {
        printf("%d ", ans[n-1][i]);
    }
    return 0;
}

