int N;

inline void update(int n, int indx, int cntS){
    if( ((cntS&1) != (lastUpdated[n][indx]&1)) && D[n][indx]){
        // conduct salsa
        D[n][indx] = 3-D[n][indx];
    }
    // update the lastUpdated index
    lastUpdated[n][indx] = cntS;
}

int pow3[16];

int main(){
    scanf("%d", &N);
    scanf(" %s", T);

    int M = strlen(T);

    pow3[0] = 1;
    for(int i = 1; i <= N; i ++){
        pow3[i] = pow3[i-1]*3;
    }

    //memset(W, 0, sizeof(W));
    for(int n = 1; n <= N; n ++){
        //printf("ok");
        for(int j = 0; j < pow3[n-1]; j ++){
            D[n][j] = 0;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        for(int j = pow3[n-1]; j < (pow3[n-1]<<1); j ++){
            D[n][j] = 1;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        for(int j = (pow3[n-1]<<1); j < pow3[n]; j ++){
            D[n][j] = 2;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        W[n][0] = pow3[n]-1;
    }

    //memset(lastUpdated, 0, sizeof(lastUpdated));
    int cntS2 = 0;
    for(int n = 1; n <= N; n ++){
        int cntS = 0;
        for(int i = 0; i < M; i ++){
            int indx0 = W[n-1][i];
            int indx1 = W[n-1][i]+pow3[n-1];
            int indx2 = W[n-1][i]+(pow3[n-1]<<1);

            if(indx1 >= pow3[n]){indx1 -= pow3[n];}
            if(indx2 >= pow3[n]){indx2 -= pow3[n];}

            update(n, indx0, cntS+(T[i]=='S'));
            update(n, indx1, cntS+(T[i]=='S'));
            update(n, indx2, cntS+(T[i]=='S'));
            if(D[n][indx0] == 2){
                W[n][i] = indx0;
            }else if(D[n][indx1] == 2){
                W[n][i] = indx1;
            }else{
                W[n][i] = indx2;
            }

            if(T[i] == 'R'){
                D[n][indx0] = (D[n][indx0]+1);
                D[n][indx1] = (D[n][indx1]+1);
                D[n][indx2] = (D[n][indx2]+1);
                if(D[n][indx0] == 3){D[n][indx0] = 0;}
                if(D[n][indx1] == 3){D[n][indx1] = 0;}
                if(D[n][indx2] == 3){D[n][indx2] = 0;}
                lastUpdated[n][indx0] = cntS;
                lastUpdated[n][indx1] = cntS;
                lastUpdated[n][indx2] = cntS;
            }else if(T[i] == 'S'){
                cntS ++;
            }

            //printf("W[%d][%d]=%d\n", n, i+1, W[n][i+1]);
        }

        cntS2 = cntS;
    }

    for(int n = 1; n <= N; n ++){
        for(int i = 0; i < pow3[n]; i ++){
            update(n, i, cntS2);
        }

        int cnt = 0;
        for(int i = 0; i < pow3[N]; i ++){
            res[i] += D[n][cnt++]*pow3[n-1];
            if(cnt == pow3[n]){cnt = 0;}
        }
    }

    for(int i = 0; i < pow3[N]; i ++){
        /*int temp = 0;
        for(int n = 1; n <= N; n ++){
            temp += D[n][i%pow3[n]]*pow3[n-1];
        }*/
        //putInt(temp);
        putInt(res[i]);
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int MAXV = 6.1e5;
const int MAXN = 13;
int p3[MAXN];
int n;

struct node {
	int idx;
	node* c[3];
	bool lazy;
};

node* init(int cur, int pos) {
	node* v = new node();
	if (cur == n) {
		// leaf
		v->idx = pos;
	} else {
		for (int x = 0; x < 3; x++) {
			v->c[x] = init(cur+1, pos + p3[cur] * x);
		}
	}
	return v;
}

void apply(node* v) {
	assert(v);
	v->lazy = !v->lazy;
	swap(v->c[1], v->c[2]);
}

void propagate(node* v) {
	if (!v->lazy) return;
	v->lazy = false;
	for (int x = 0; x < 3; x++) apply(v->c[x]);
}

void upd(node* v, int cur) {
	if (cur == n) {
		// leaf
	} else {
		propagate(v);
		swap(v->c[1], v->c[2]);
		swap(v->c[0], v->c[1]);
		upd(v->c[0], cur+1);
	}
}

int ans[MAXV];

void dfs(node* v, int cur, int pos) {
	if (cur == n) {
		ans[v->idx] = pos;
	} else {
		propagate(v);
		for (int x = 0; x < 3; x++) dfs(v->c[x], cur+1, pos + x * p3[cur]);
	}
}

int main() {
	ios::sync_with_stdio(0), cin.tie(0);
	string t;
	cin >> n >> t;
	p3[0] = 1;
	for (int i = 1; i <= n; i++) p3[i] = p3[i-1] * 3;
	node* tr = init(0, 0);
	for (char op : t) {
		if (op == 'R') {
			upd(tr, 0);
		} else if (op == 'S') {
			apply(tr);
		} else assert(false);
	}
	dfs(tr, 0, 0);
	for (int i = 0; i < p3[n]; i++) {
		cout << ans[i] << " \n"[i+1==p3[n]];
	}
}

