int n;

int rev(int x) {
  static int dig[20];
  for (int i = 0; i < n; ++i) {
    dig[i] = x % 3;
    x /= 3;
  }
  int ret = 0;
  for (int i = 0; i < n; ++i)
    ret = ret * 3 + dig[i];
  return ret;
}

int ch[1000005][3];
bool tag[1000005];
int val[1000005], ans[1000005];

int cnt, v;

int build(int k) {
  int o = ++cnt;
  if (k == 0) {
    val[o] = rev(v++);
    return o;
  }
  ch[o][0] = build(k - 1);
  ch[o][1] = build(k - 1);
  ch[o][2] = build(k - 1);
  return o;
}

void pd(int o) {
  if (tag[o]) {
    tag[o] = false;
    swap(ch[o][1], ch[o][2]);
    for (int i = 0; i < 3; ++i) tag[ch[o][i]] ^= 1;
  }
}

void ad(int o, int k) {
  if (k == 0)
    return;
  pd(o);
  swap(ch[o][1], ch[o][2]);
  swap(ch[o][0], ch[o][1]);
  ad(ch[o][0], k - 1);
}

int dfn;
void dfs(int o, int k) {
  if (k == 0) {
    ans[val[o]] = rev(dfn++);
    return;
  }
  pd(o);
  for (int i = 0; i < 3; ++i)
    dfs(ch[o][i], k - 1);
}

