int sch(int n){
    int out=0,tmp,count=0;
    while(n != 0){
        tmp = n % 3;
        if(tmp==1){
            out += 2*pow(3,count);
        }else if(tmp==2){
            out += pow(3,count);
        }
        count++;
        n /= 3;
    }
    return out;
}

