  to[0] = 1;
  range(i, 1, n + 1) to[i] = to[i - 1] * 3;

  string a;
  cin >> a;
  int sz = a.length();

  vector<int> p; // 3^n
  vector<int> pos; // sz
  p.resize(1);
  pos.resize(sz);

  for(int ex = 1; ex <= n; ex++) {
    int total = 0;
    vector<int> add(to[ex]);
    range(i, 0, to[ex]) add[i] = i / to[ex - 1];

    p.resize(to[ex]);
    range(i, to[ex - 1], to[ex]) p[i] = p[i % to[ex - 1]];

    vector<int> new_pos(sz);

    for(int i = 0; i < sz; i++) {
      {
        int ps = pos[i];
        assert(~ps);
        for(int j = ps; j <= ps + 2 * to[ex - 1]; j += to[ex - 1]) {
          int c = add[j];
          if(total) {
            if(c > 0) c = 3 - c;
          }
          if(c == 2) new_pos[i] = j;
        }
      }
      char com = a[i];
      if(com == 'S') {
        total ^= 1;
      } else {
        int ps = pos[i];
        assert(~ps);
        int ter = -1;
        for(int j = ps; j <= ps + 2 * to[ex - 1]; j += to[ex - 1]) {
          int c = add[j];
          if(total) {
            if(c > 0) c = 3 - c;
          }
          c = (c + 1) % 3;
          if(total) {
            if(c > 0) c = 3 - c;
          }
          add[j] = c;
        }
      }
    }

    if(total) {
      for(int i = 0; i < to[ex]; i++) {
        if(add[i] > 0) add[i] = 3 - add[i];
      }
    }

    for(int i = 0; i < to[ex]; i++) {
      p[i] += add[i] * to[ex - 1];
    }

    swap(pos, new_pos);
  }
  for(int i : p) cout << i << " ";
  return 0;
}

