int main() {
   ios_base::sync_with_stdio(false); cin.tie(nullptr);
   int N; string T;
   cin >> N >> T;
   vector<int> perm(round(pow(3, N)));
   auto simplify = [&](string T) {
      string newT;
      for (char c : T) {
         if (c == 'R') {
            newT += c;
         } else if (newT.size() && newT.back() == 'S') {
            newT.pop_back();
         } else {
            newT += c;
         }
      }
      return newT;
   };
   function<void(int, string, int, int, int)> solve = [&](int bit, string T, int pw, int from, int to) {
      if (bit == N) {
         perm[from] = to;
         return;
      }
      for (int d : {0, 1, 2}) {
         int nd = d;
         string newT;
         for (char c : T) {
            if (c == 'S') {
               newT += c;
               if (nd) nd = 3 - nd;
            } else {
               if (nd == 2) {
                  newT += c;
               }
               nd = (nd + 1) % 3;
            }
         }
         solve(bit + 1, simplify(newT), pw * 3, from + d * pw, to + nd * pw);
      }
   };
   solve(0, T, 1, 0, 0);
   for (int v : perm) cout << v << " "; cout << "\n";
}

