string sarusa(string s)
{
    rep(i, s.length())
    {
        if (s[i] == '1')
        {
            s[i] = '2';
        }
        else if (s[i] == '2')
        {
            s[i] = '1';
        }
    }
    return s;
}

string addOne(string s)
{
    s[s.length() - 1]++;
    rep(i, s.length())
    {
        if (s[s.length() - 1 - i] == '3')
        {
            s[s.length() - 1 - i] = '0';
            if (i < s.length() - 1)
                s[s.length() - 2 - i]++;
        }
        else
        {
            break;
        }
    }
    return s;
}

