const int maxN = (1 << 20);
int n;
string op;
int ch[maxN][3], id[maxN];
int cn = 1, rt;

bool flip = false;
int get(int x) {
	if(x == 0) return x;
	if(flip) return 3 - x; else return x;
}

void build(int &o, int dep, int val, int dval) {
	o = ++cn;
	if(dep == 0) {
		id[o] = dval;
	} else {
		id[o] = 0;
		for(int i = 0; i < 3; ++i) build(ch[o][get(i)], dep - 1, val * 3, i * val + dval);
	}
}

vector<int> q;
void dfs(int o, int dep, int val, int dval) {
	if(dep == 0) {
		q[id[o]] = dval;
	} else {
		for(int i = 0; i < 3; ++i) dfs(ch[o][get(i)], dep - 1, val * 3, i * val + dval);
	}
}

