			while(dep) {
				int &x = ch[o][get(0)], &y = ch[o][get(1)], &z = ch[o][get(2)];
				int t;
				t = x; x = z; z = y; y = t;
				o = x;
				--dep;
			}
		}
	}
	q.assign(cnt, 0);
	dfs(rt, n, 1, 0);
	for(int i = 0; i < (int)q.size(); ++i) cout << q[i] << " \n"[i + 1 == (int)q.size()];
