					}
					F[y]=nf;
				}
				FOR(j,3) {
					D[y+j*p3[i-1]]=(D[y+j*p3[i-1]]+1)%3;
				}
			}
			y=W[i-1][x+1];
			if((nf-F[y])%2) {
