				F[y]=nf;
			}
			FOR(j,3) if(D[y+j*p3[i-1]]==2) W[i][x+1]=y+j*p3[i-1];
		}


		FOR(y,p3[i-1]) {
			if((nf-F[y])%2) {
				FOR(j,3) {
					if(D[y+j*p3[i-1]]==1) D[y+j*p3[i-1]]=2;
					else if(D[y+j*p3[i-1]]==2) D[y+j*p3[i-1]]=1;
				}
			}
		}
