string memo[12][59049*9];
int val[12][59049 * 9];

int rev[59049 * 9];
void init() {
	rep(i, 59049 * 9) {
		vector<int> v;
		int cop = i;
		while (cop > 0) {
			v.push_back(cop % 3); cop /= 3;
		}
		rep(j, v.size())if (v[j] != 0)v[j] = 3 - v[j];
		rev[i] = 0;
		int s = 1;
		rep(j, v.size()) {
			rev[i] += s * v[j]; s *= 3;
		}
	}

}

string normalize(string &t) {
	string cop;
	rep(i, t.size()) {
		if (cop.size() && cop.back() == 'S'&&t[i] == 'S') {
			cop.pop_back();
		}
		else{
			cop.push_back(t[i]);
		}
	}
	return cop;
}

void solve(){
	int n; cin >> n;
	string t; cin >> t;
	t = normalize(t);
	rep(j, 3)memo[0][j] = t;


	rep(i, n-1) {
		int sup = 1;
		rep(j, i + 1)sup *= 3;
		rep(j, sup) {
			string nex;
			int cur = j / (sup / 3);
			for (char c : memo[i][j]) {
				if (c == 'S') {
					nex.push_back('S');
					if (cur != 0)cur = 3 - cur;
				}
				else {
					cur++;
					if (cur == 3) {
						cur = 0;
						nex.push_back('R');
					}
				}
			}
			nex = normalize(nex);
			rep(k, 3) {
				memo[i + 1][j + k * sup] = nex;
			}
		}
	}
	rep(i, n) {
		int sup = 1;
		rep(j, i + 1)sup *= 3;
		rep(j, sup) {
			int cur = j / (sup / 3);
			for (char c : memo[i][j]) {
				if (c == 'S') {
					if (cur != 0)cur = 3 - cur;
				}
				else {
					cur++; if (cur == 3)cur = 0;
				}
			}
			val[i][j] = cur;
		}
	}
	//rep(j, 3)cout << memo[0][j] << "\n";
	//rep(j, 3)cout << val[0][j] << "\n";
	vector<int> ans;
	int tn = 1; rep(i, n)tn *= 3;
	rep(i, tn) {
		int s = 1;
		int x = 0;
		rep(j, n) {
			x += s * val[j][i % (s * 3)];
			s *= 3;
		}
		ans.push_back(x);
	}
	rep(i, ans.size()) {
		if (i > 0)cout << " ";
		cout << ans[i];
	}
	cout << "\n";
}


